define('siae/views/FournisseursTableView',['siae/Common', 'siae/views/FournisseurLigneView'], function(Common, FournisseurLigneView) {
	
	var FournisseursTableView = Backbone.View.extend({
		tagName:"div",
		id:"fournisseursTableView",
		initialize: function() {
			var self = this;
			_.bindAll(this, 'render');
			this.subViews = _.map(this.collection.models, function(fournisseur) { 
				return new FournisseurLigneView({ model: fournisseur, parent: self	});
			});
		},
		render: function() {
			var self = this;
			$("#contenu").html(this.el);
			//TODO template
			$(this.el).append('<table id="ajouterFournisseur"><tr><td>Ajouter fournisseur&nbsp;</td><td>'
					+'<a href="#editerFournisseur/nouveau" class="action creation"></a></td></tr></table>'
					+'<table id="fournisseursTable"><thead><th>Code</th><th>Nom</th>'
					+'<th>Pays</th><th>R&ocirc;le</th><th>Action</th></thead></table>');
			_.each(this.subViews, function(view) {
				$('#fournisseursTable', self.el).append(view.render().el);
			}, this);
			this.datatable = $('#fournisseursTable', this.el).dataTable({ bJQueryUI: true });
		},
		supprimerLigneFournisseur: function(row) {
			this.datatable.fnDeleteRow(row);
		},
		close: function() {
			_.each(this.subViews, function(view) { view.remove(); });
			this.remove();
		}
	});
	
	return FournisseursTableView;

});

