define('siae/routers/FournisseurRouter',['siae/Common','siae/views/FournisseurView','siae/views/FournisseursTableView',
        'siae/views/FournisseurEditerView', 'siae/models/Fournisseur', 'siae/collections/Fournisseurs'], 
        function(Common, FournisseurView, FournisseursTableView, FournisseurEditerView, Fournisseur, Fournisseurs) {
	
	var FournisseurRouter = Backbone.Router.extend({
		routes : {
			"fournisseurs" : "listerFournisseurs",
			"fournisseur/:id" : "voirFournisseur",
			"editerFournisseur/:id" : "creerOuEditerFournisseur",
			'*path':  'defaultRoute'
		},
		listerFournisseurs : function() {
			var self = this;
			var fournisseurs = new Fournisseurs();
			fournisseurs.fetch({
				success: function(fournisseurs) {
					self.loadView(new FournisseursTableView({ collection: fournisseurs }));
				}
			});
		},
		voirFournisseur : function(id) {
			var self = this;
			var fournisseur = new Fournisseur({ id: id });
			fournisseur.fetch({
				success: function(fournisseur) {
					self.loadView(new FournisseurView({	model: fournisseur }));
				}
			});
		},
		creerOuEditerFournisseur : function(id) {
			if ("nouveau" == id) {
				this.loadView(new FournisseurEditerView({ model: new Fournisseur() }));
			}
			else {
				var self = this;
				var fournisseur = new Fournisseur({ id: id });
				fournisseur.fetch({
					success: function(fournisseur) {
						self.loadView(new FournisseurEditerView({ model: fournisseur }));
					}
				});
			}
			//this.loadView(new UsersView());
		},
		loadView : function(view) {
			this.view && (this.view.close ? this.view.close() : this.view.remove());
			this.view = view;
			this.view.render();
		},
		defaultRoute: function() {
			this.listerFournisseurs();
		}
		
	});
	
	return FournisseurRouter;

});

